const fs = require('fs')
const wikipedia = require('./wikipedia')
module.exports={
    wikipedia,
    calculator: function(num1, sum, num2){
       if(!num1) throw new TypeError("No num1 was specified!") 
       if(!sum) throw new TypeError("No sum was specified!") 
       if(!num2) throw new TypeError("No num2 was specified!") 
       if(sum=='*') return num1*num2
       if(sum=='+') return num1+num2
       if(sum=='/') return num1/num2
       if(sum=='-') return num1-num2
    },
    json: function(path, val){
        if(!path) throw new TypeError("No path was specified!") 
        let file = require(path)
        return file.hi
    },
    replace: function(string,regex,final){
        if(!string) throw new TypeError("No string was specified!")
        if(!regex) throw new TypeError("No regex was specified!")
        if(!final) throw new TypeError("No final replacement was specified!")
        return string.replace(regex,final)

    }
}